INSERT INTO `tbldonacion` (`ID`, `DonacionTitle`, `DonacionDescription`, `Image`, `CreationDate`) VALUES
(3, 'Ropa cómoda y de fácil uso', '<p class=\"MsoNormal\">Ropa cómoda y de fácil uso: camisas, pantalones, suéteres,\r\ncalcetines, batas, pijamas, etc. Asegúrate de que sean prendas nuevas o en buen\r\nestado.<o:p></o:p></p>', '8c458e3489329de9532ce70409eeef1e1719262495.jpg', '2024-06-24 20:54:55'),
(4, 'Artículos de cuidado personal', '<p class=\"MsoNormal\">Artículos de cuidado personal: champú, acondicionador,\r\njabón, crema de afeitar, cepillos de dientes, pasta dental, desodorante,\r\ntoallas, etc.<o:p></o:p></p>', '0682277bf11cd3fd6bbf5053ca3ec7d31719262551.jpg', '2024-06-24 20:55:51'),
(5, 'Libros y revistas', '<p class=\"MsoNormal\">Artículos para el entretenimiento: libros, revistas, juegos\r\nde mesa, rompecabezas, naipes, audífonos, radios pequeños.<o:p></o:p></p>', 'e20584d78a8a960c6daab0f17d9ca1f01719262609.jpg', '2024-06-24 20:56:49'),
(6, 'Artículos para el hogar', '<p class=\"MsoNormal\">Artículos para el hogar: floreros, portarretratos, mantas,\r\nalmohadas, cortinas.<o:p></o:p></p>', 'f37c855f387ae3f3c39e5762199e6cbe1719263142.jpg', '2024-06-24 20:57:36'),
(7, 'Artículos para actividades', '<p class=\"MsoNormal\">Artículos para actividades: material de manualidades,\r\ninsumos para actividades recreativas, rompecabezas, pinturas, lápices de\r\ncolores.<o:p></o:p></p>', '90d3b79e47958712aa37c4daf3edd58b1719262737.jpg', '2024-06-24 20:58:57'),
(8, 'Electrodomésticos', '<span style=\"font-size:11.0pt;line-height:107%;\r\nfont-family:&quot;Calibri&quot;,sans-serif;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:\r\nCalibri;mso-fareast-theme-font:minor-latin;mso-hansi-theme-font:minor-latin;\r\nmso-bidi-font-family:&quot;Times New Roman&quot;;mso-bidi-theme-font:minor-bidi;\r\nmso-ansi-language:ES-SV;mso-fareast-language:EN-US;mso-bidi-language:AR-SA\">Electrodomésticos\r\npequeños: cafeteras, tostadoras, licuadoras (en buen estado).</span><br>', '33c0e0cc776e298cd6d866cff59891341719262806.jpg', '2024-06-24 20:59:42'),
(9, 'Granos Básicos', '<p class=\"MsoNormal\">Arroz - Es un alimento básico y muy versátil. Donaciones de\r\narroz blanco o integral son siempre apreciadas.<o:p></o:p></p>\r\n\r\n<p class=\"MsoNormal\">Frijoles - Ricos en proteínas y fibra. Pueden ser frijoles\r\nsecos o enlatados.<o:p></o:p></p>\r\n\r\n<p class=\"MsoNormal\">Lentejas - Otra buena fuente de proteínas vegetales.<o:p></o:p></p>\r\n\r\n<p class=\"MsoNormal\">Avena - Una opción saludable y nutritiva para el desayuno.<o:p></o:p></p>\r\n\r\n<p class=\"MsoNormal\">Pastas - Espaguetis, macarrones, penne, etc. Ayudan a\r\ncomponer comidas saludables.<o:p></o:p></p>\r\n\r\n<p class=\"MsoNormal\">Harina de trigo - Útil para cocinar y hornear.<o:p></o:p></p>\r\n\r\n<p class=\"MsoNormal\">Cereales integrales - Aportan fibra y minerales. Busca\r\nopciones con poco azúcar.<o:p></o:p></p>\r\n\r\n<p class=\"MsoNormal\">Quinoa - Un grano rico en proteínas, fibra y minerales.<o:p></o:p></p>', 'acbcc06190dc52b2eeb0ada6efac826d1719262870.jpg', '2024-06-24 21:01:10'),
(10, 'Medicamentos ', '<p class=\"MsoNormal\">Medicamentos de venta sin receta, como analgésicos,\r\nantiácidos, descongestionantes, etc. Asegúrate de que estén dentro de la fecha\r\nde caducidad.<o:p></o:p></p>\r\n\r\n<p class=\"MsoNormal\">Medicamentos con receta que aún tengan validez y que se\r\nhayan almacenado correctamente. Consulta con el hogar sobre los medicamentos\r\nque necesitan.<o:p></o:p></p>\r\n\r\n<p class=\"MsoNormal\">Suplementos vitamínicos y minerales.<o:p></o:p></p>', '631c9ad213c119a2dd09a0fbf893cafa1719263000.jpg', '2024-06-24 21:03:20');

